        }

        .history-item-amount {
          font-size: 1.2rem;
          font-weight: bold;
          color: #667eea;
        }

        .no-history {
          text-align: center;
          color: #666;
          font-style: italic;
          padding: 40px;
        }

        .loading-spinner {
          display: flex;
          flex-direction: column;
          align-items: center;
